import React, { useState } from "react";
import { Container } from '@mui/material'; // Unused imports removed for simplicity

export default function Sports() {
    const [isVisible, setIsVisible] = useState(false); // useState imported from React

    const toggleVisibility = () => {
        setIsVisible(!isVisible); // Correctly toggling visibility
    };

    return (
        <div>
            <Container disableGutters maxWidth={false} className='dark_gray_bg pb_50 pt_100 card_wrapper fun_trainer'>
                <Container fixed className='max_width_1100'>
                    <button onClick={toggleVisibility}>
                        {isVisible ? 'Hide' : 'Show'}
                    </button>

                    {/* Display block or none based on the state */}
                    <div style={{ display: isVisible ? 'block' : 'none' }}>
                        This is the content to toggle!
                    </div>
                </Container>
            </Container>
        </div>
    );
}
