import React from "react";
import { Container, Grid, Typography, TextField, TextareaAutosize } from '@mui/material';
import contact_form from '../images/contact_form.png';
import { useState } from "react";


export default function Contact() {
    // Set initial form state
    const [formData, setFormData] = useState({
      name: '',
      email: '',
      phone: '',
      message: ''
    });

    // Handle form input changes
    const handleChange = (e) => {
      setFormData({
        ...formData,
        [e.target.name]: e.target.value
      });
    };

    // Handle form submission
    const handleSubmit = (e) => {
      e.preventDefault();
      console.log('Form Data Submitted:', formData);
      // Add form submission logic (e.g., send data to API)

      if (!formData.name || !formData.email) {
        alert('Please fill in all fields');
        return;
      }

      console.log('Form Data Submitted:', formData);
      alert("Your Form Submitted");
    };


    return(
        <div>
            <Container disableGutters maxWidth={false} className='dark_gray_bg pb_50 pt_100 contact_form'>
<Container fixed>

<Grid container spacing={5}>
<Grid item xs={6}>
<img src={contact_form} alt="Contact" />
    </Grid>
    <Grid item xs={6}>

    <Typography gutterBottom variant="h4" component="div">
Get in touch
</Typography>
<form onSubmit={handleSubmit}>

<TextField fullWidth label="Name" value={formData.name} onChange={handleChange} />
<TextField fullWidth label="Email" value={formData.email} onChange={handleChange} />
<TextField fullWidth label="Phone" value={formData.phone} onChange={handleChange} />
<TextareaAutosize fullWidth minRows={4} style={{ width: "100%" }} value={formData.message} onChange={handleChange} />
  <button type="submit">Submit</button>

    </form>
    </Grid>
    </Grid>
    </Container>
    </Container>
        </div>
    )
}
